import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets

def flowFile = session.get()
if (!flowFile) return

// Define the list of attribute keys you want to include in the transformed string
def attributeKeys = ['grant_type', 'scope', 'client_id', 'username', 'password']

// Initialize an empty string to hold the concatenated attributes
def result = ""

// Get the flow file attributes
def attributes = flowFile.getAttributes()

// Iterate over the specified attributes and concatenate their values with '&'
attributeKeys.each { key ->
    def value = attributes[key]
    if (value) {
        result += key + "=" + value + "&"
    }
}

// Remove the last '&' from the result string
if (result.endsWith("&")) {
    result = result.substring(0, result.length() - 1)
}

// Add the new attribute "get_access_token_form_data" with the transformed string value
flowFile = session.putAttribute(flowFile, 'get_access_token_form_data', result)

// Convert the result string to bytes and create a new flow file with the transformed content
def output = result.getBytes(StandardCharsets.UTF_8)
flowFile = session.write(flowFile, { inputStream, outputStream ->
    outputStream.write(output)
} as StreamCallback)

// Transfer the flow file to the success relationship
session.transfer(flowFile, REL_SUCCESS)

import java.time.LocalDate;
import java.time.YearMonth;
import org.apache.commons.lang3.StringUtils;

def flowFile = session.get();
if (flowFile == null) {
    return;
}

def year = flowFile.getAttribute('crawling_year');
def month = flowFile.getAttribute('crawling_month');

if (StringUtils.isNumeric(year) && StringUtils.isNumeric(month)) {
    int yearInt = year.toInteger();
    int monthInt = month.toInteger();

    if (monthInt >= 1 && monthInt <= 12) {
        LocalDate endOfMonth = YearMonth.of(yearInt, monthInt).atEndOfMonth();
        def endDayOfMonth = endOfMonth.getDayOfMonth();
        def formattedEndDay = StringUtils.leftPad(endDayOfMonth.toString(), 2, '0');
        flowFile = session.putAttribute(flowFile, 'end_day_of_crawling_month', formattedEndDay);
    }
}

session.transfer(flowFile, REL_SUCCESS);

